package leetcode

import org.junit.Test

/**
 * @Author bruce.ge
 * @Date 2018/4/28
 * @Description
 */
class MaxConsecutiveOnes {
    fun findMaxConsecutiveOnes(nums: IntArray): Int {
        var max = 0;
        var k = 0;
        for (i in 0..nums.size - 1) {
            if (nums[i] == 1) {
                k++;
            } else {
                if (max < k) {
                    max = k;
                }
                k = 0;
            }
        }
        if (k > max) {
            max = k;
        }
        return max;
    }


